package Module1.ArraysClass;

public class SpecialSubsequence {
  
    public static void main(String[] args) {

        String s = "";
        
        int count = findCount(s);
        System.out.println(count);

        int countOfPairs = findPairsCount(s);
        System.out.println(countOfPairs);


    }

    private static int findPairsCount(String s) {

        int pairCount = 0;
        int noOfG = 0;

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) =='G') {
                 noOfG++;
            }
        }
        System.out.println(noOfG);
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == 'A') {
                pairCount+=noOfG;
            }
            if (s.charAt(i) == 'G') {
                noOfG--;
            }
         
        }
        return pairCount;
       
    }

    /**
     * Bruitforce Approach
     */
    public static int findCount(String s){
        
        int count = 0;
        for (int i = 0; i < s.length()-1; i++) {
            if (Character.toUpperCase(s.charAt(i)) == 'A') {
                for (int j = i + 1; j < s.length(); j++) {
                    if (Character.toUpperCase(s.charAt(j)) == 'G') {
                        count++;
                    }
                }
            }
        }
        return count;
    }
}
